import React, { useState } from 'react';
import InvestForm from './Components/InvestForm';
import Header from './Components/Header';
import ResultTable from './Components/ResultTable';
import logo from './assets/investment-calculator-logo.png';
const data1=[];
function App() {
  const[yearlyData,setYearlyData]=useState(data1);
  const[showTable,setShowTable]=useState(false);

  const calculateHandler = (userInput) => {
    setShowTable(true);
    userInput.preventDefault();
    console.log("submitted!");
    // Should be triggered when form is submitted
    // You might not directly want to bind it to the submit event on the form though...

    // const yearlyData = []; // per-year results
    let currentSavings = Number(userInput.target[0].value); // feel free to change the shape of this input object!
    const yearlyContribution = Number(userInput.target[1].value) // as mentioned: feel free to change the shape...
    const expectedReturn = Number(userInput.target[2].value) / 100;
    const duration = Number(userInput.target[3].value);
    // The below code calculates yearly results (total savings, interest etc)
    const arr=[];
    let prev=0;
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      const val={
        // feel free to change the shape of the data pushed to the array!
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
        prev:prev
      };
      prev+=yearlyInterest;
      arr.push(val);
    }
    setYearlyData(arr);
    // do something with yearlyData ...
  };

  return (
    <div>
      <Header logo={logo}/>
      <InvestForm submitHandler={calculateHandler}/>
      {showTable && <ResultTable data={yearlyData}/>}
      {/* Show fallback text if no data is available */}

    </div>
  );
}

export default App;
